import React, { useState } from 'react';
import {
  Container,
  MainPanel,
  Header,
  RobotCanvas,
  ControlPanel,
  Button,
  ModuleStatus,
  ColorPicker,
  ToolsDecoration,
  LightBulbs
} from './styles';
import Robot3D from './components/Robot3D';

interface RobotParts {
  head: number;
  body: number;
  arms: number;
  legs: number;
  color: string;
  accessories: {
    hat: boolean;
    mustache: boolean;
    lipstick: boolean;
  };
}

const App: React.FC = () => {
  const [robotParts, setRobotParts] = useState<RobotParts>({
    head: 1,
    body: 1,
    arms: 1,
    legs: 1,
    color: '#4fc3f7',
    accessories: {
      hat: false,
      mustache: false,
      lipstick: false
    }
  });

  const updatePart = (part: keyof RobotParts) => {
    if (typeof robotParts[part] === 'number') {
      setRobotParts(prev => ({
        ...prev,
        [part]: (prev[part] as number % 5) + 1,
      }));
    }
  };

  const toggleAccessory = (accessory: keyof RobotParts['accessories']) => {
    setRobotParts(prev => ({
      ...prev,
      accessories: {
        ...prev.accessories,
        [accessory]: !prev.accessories[accessory]
      }
    }));
  };

  const [completedModules, setCompletedModules] = useState(0);

  return (
    <Container>
      <MainPanel>
        <Header>
          <h1>ROBO-BEERTENDER GARAGE</h1>
        </Header>

        <RobotCanvas color={robotParts.color}>
          <ToolsDecoration />
          <LightBulbs>
            <div className="bulb" />
            <div className="bulb" />
            <div className="bulb" />
          </LightBulbs>
          <Robot3D parts={robotParts} />
        </RobotCanvas>

        <ControlPanel>
          <Button onClick={() => updatePart('head')}>HEAD</Button>
          <Button onClick={() => updatePart('body')}>BODY</Button>
          <Button onClick={() => updatePart('arms')}>ARMS</Button>
          <Button onClick={() => updatePart('legs')}>LEGS</Button>
        </ControlPanel>

        <ControlPanel>
          <Button onClick={() => toggleAccessory('hat')}>
            {robotParts.accessories.hat ? 'REMOVE HAT' : 'ADD HAT'}
          </Button>
          <Button onClick={() => toggleAccessory('mustache')}>
            {robotParts.accessories.mustache ? 'REMOVE MUSTACHE' : 'ADD MUSTACHE'}
          </Button>
          <Button onClick={() => toggleAccessory('lipstick')}>
            {robotParts.accessories.lipstick ? 'REMOVE LIPSTICK' : 'ADD LIPSTICK'}
          </Button>
        </ControlPanel>

        <ColorPicker
          type="color"
          value={robotParts.color}
          onChange={(e) => setRobotParts(prev => ({ ...prev, color: e.target.value }))}
        />

        <Button onClick={() => {
          const randomPart = () => Math.floor(Math.random() * 5) + 1;
          setRobotParts({
            head: randomPart(),
            body: randomPart(),
            arms: randomPart(),
            legs: randomPart(),
            color: `#${Math.floor(Math.random()*16777215).toString(16)}`,
            accessories: {
              hat: Math.random() > 0.5,
              mustache: Math.random() > 0.5,
              lipstick: Math.random() > 0.5
            }
          });
        }}>
          RANDOMIZE CONFIGURATION
        </Button>

        <ModuleStatus>
          {completedModules} / 7 MODULES COMPLETED
        </ModuleStatus>
      </MainPanel>
    </Container>
  );
};

export default App;
